import React, { useState, useEffect } from 'react';
import CreateJob from '../components/HR/CreateJob';
import UpdateApplication from '../components/HR/UpdateApplication';
import API from '../api';

const HRDashboard = () => {
    const [jobs, setJobs] = useState([]);
    const [selectedJob, setSelectedJob] = useState(null);

    useEffect(() => {
        const fetchJobs = async () => {
            try {
                const { data } = await API.get('/jobs'); // Fetch jobs from the backend
                setJobs(data);
            } catch (error) {
                console.error('Failed to fetch jobs:', error.response?.data || error.message);
                alert('Failed to fetch jobs. Please check the console for more details.');
            }
        };

        fetchJobs();
    }, []);

    return (
        <div>
            <h1>HR Dashboard</h1>
            <CreateJob />
            <h2>Job Listings</h2>
            {jobs.length > 0 ? (
                jobs.map((job) => (
                    <div key={job._id} style={{ border: '1px solid #ccc', padding: '10px', margin: '10px 0' }}>
                        <h3>{job.title}</h3>
                        <p>{job.description}</p>
                        <button onClick={() => setSelectedJob(job._id)}>View Applications</button>
                    </div>
                ))
            ) : (
                <p>No jobs available. Create a new job to get started.</p>
            )}
            {selectedJob && <UpdateApplication jobId={selectedJob} />}
        </div>
    );
};

export default HRDashboard;
