import React from 'react';
import CreateJob from '../components/HR/CreateJob';
import JobList from '../components/HR/JobList';
import UpdateApplication from '../components/HR/UpdateApplication';

const HRDashboard = () => {
    return (
        <div>
            <h1>HR Dashboard</h1>
            <CreateJob />
            <JobList />
            {/* Pass a specific jobId to UpdateApplication for testing */}
            <UpdateApplication jobId="sample-job-id" />
        </div>
    );
};

export default HRDashboard;
