import React, { useEffect, useState } from 'react';
import API from '../../api';

const UpdateApplication = ({ jobId }) => {
    const [applications, setApplications] = useState([]);
    const [loading, setLoading] = useState(false);

    // Fetch applications on load
    const fetchApplications = async () => {
        try {
            const { data } = await API.get(`/applications/${jobId}`);
            setApplications(data);
        } catch (error) {
            console.error('Error fetching applications:', error);
        }
    };

    useEffect(() => {
        if (jobId) {
            fetchApplications();
        }
    }, [jobId]);

    // Update status and send email if accepted
    const updateStatus = async (applicationId, newStatus, applicantEmail, applicantName) => {
        setLoading(true);
        try {
            await API.patch(`/jobs/update-status/${jobId}/${applicationId}`, { status: newStatus });

            // If Accepted, send email
            if (newStatus === 'accepted') {
                await API.post('/send-email', {
                    to: applicantEmail,
                    name: applicantName,
                    jobId: jobId,
                });
            }

            setApplications(applications.map(app =>
                app._id === applicationId ? { ...app, status: newStatus } : app
            ));

            alert(`Application ${newStatus} successfully!`);
        } catch (error) {
            console.error('Error updating status:', error);
            alert('Failed to update application');
        }
        setLoading(false);
    };

    return (
        <div>
            <h2>Applications</h2>
            {loading && <p>Processing...</p>}
            {applications.length > 0 ? (
                applications.map(app => (
                    <div key={app._id} style={{ border: '1px solid #ccc', padding: '10px', margin: '10px 0' }}>
                        <p><strong>Name:</strong> {app.userId.name}</p>
                        <p><strong>Email:</strong> {app.userId.email}</p>
                        <p>
                            <strong>Resume:</strong> <a href={`http://localhost:5000/${app.resume}`} target="_blank" rel="noopener noreferrer">View</a>
                        </p>
                        <p><strong>Status:</strong> {app.status}</p>
                        <button
                            style={{ backgroundColor: '#f0ad4e', color: 'white', marginRight: '5px' }}
                            onClick={() => updateStatus(app._id, 'waiting', app.userId.email, app.userId.name)}
                        >
                            Waiting
                        </button>
                        <button
                            style={{ backgroundColor: '#5cb85c', color: 'white', marginRight: '5px' }}
                            onClick={() => updateStatus(app._id, 'accepted', app.userId.email, app.userId.name)}
                        >
                            Accept
                        </button>
                        <button
                            style={{ backgroundColor: '#d9534f', color: 'white' }}
                            onClick={() => updateStatus(app._id, 'rejected', app.userId.email, app.userId.name)}
                        >
                            Reject
                        </button>
                    </div>
                ))
            ) : (
                <p>No applications found.</p>
            )}
        </div>
    );
};

export default UpdateApplication;
