import React, { useEffect, useState } from 'react';
import API from '../../api';

const UpdateApplication = ({ jobId }) => {
    const [applications, setApplications] = useState([]);

    useEffect(() => {
        const fetchApplications = async () => {
            try {
                const { data } = await API.get(`/jobs/${jobId}/applications`);
                setApplications(data.applications);
            } catch (error) {
                console.error('Error fetching applications:', error);
            }
        };

        fetchApplications();
    }, [jobId]);

    const updateStatus = async (applicationId, newStatus) => {
        try {
            await API.patch(`/jobs/update-status/${jobId}/${applicationId}`, { status: newStatus });
            setApplications(applications.map(app =>
                app._id === applicationId ? { ...app, status: newStatus } : app
            ));
        } catch (error) {
            console.error('Error updating status:', error);
        }
    };

    return (
        <div>
            <h2>Applications</h2>
            {applications.map(app => (
                <div key={app._id}>
                    <p>Name: {app.userId.name}</p>
                    <p>Email: {app.userId.email}</p>
                    <p>
                        Resume: <a href={`http://localhost:5000/uploads/${application.resume}`}  target="_blank" rel="noopener noreferrer">View</a>
                    </p>
                    <p>Status: {app.status}</p>
                    <button onClick={() => updateStatus(app._id, 'waiting')}>Waiting</button>
                    <button onClick={() => updateStatus(app._id, 'accepted')}>Accept</button>
                    <button onClick={() => updateStatus(app._id, 'rejected')}>Reject</button>
                </div>
            ))}
        </div>
    );
};

export default UpdateApplication;
